import styled from "styled-components";

export const InputComponent = styled.input`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 13px 12px 13px 16px;
  width: 320px;
  height: 50px;
  border: 1px solid #a8a8b3;
  box-sizing: border-box;
  border-radius: 8px;
  font-family: Roboto;
  font-style: normal;
  font-weight: normal;
  font-size: 16px;
  line-height: 19px;
  color: #a8a8b3;
  flex: none;
  order: 0;
  flex-grow: 0;
  margin: 0px 10px;
`;
