import styled from "styled-components";

export const Header = styled.header`
  background-color: #835afd;
  width: 100%;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 20px;
`;

export const Search = styled.div`
  color: #fff;
  display: flex;
  align-items: center;
`;

export const Container = styled.div`
  display: flex;
  gap: 100px;
`;
